package stringProgrammes;

public class TestSubstring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="LoicIpanga";
		String s1= "  Kanama ";
		System.out.println(s.substring(4)); //end index
		System.out.println(s.substring(0,7)); //start index
		// Trim Example
		System.out.println(s1);
		System.out.println(s1.trim()); //Eliminate space at beginning and end
		int a=10;
		String z= String.valueOf(a);
		System.out.println(z+20);
		
	}

}
